<?php //error_reporting(0);session_start();include("../lib/connection/db-config.php");include("../lib/class/user-class.php");$setCounter = 0;$userObj=new user();$setCounter = 0;$month=$_SESSION['month'];$comp_id=$_SESSION['comp_id'];$user_id=$_SESSION['log_id'];$clintid=$_SESSION['clintid'];$setExcelName = "employee_detail";$emp=$_REQUEST['emp'];$frdt=$_REQUEST['from_date'];$frdt=date('Y-m-d',strtotime($frdt));$to_date=$_REQUEST['to_date'];$to_date=date('Y-m-d',strtotime($to_date));$res=$userObj->showEmployeereport($comp_id,$user_id);$resclt=$userObj->displayClient($clintid);$cmonth=$resclt['current_month'];if($month=='current'){	$monthtit =  date('F Y',strtotime($cmonth));    //$frdt=$cmonth;    //$todt=$cmonth;    $tab_emp='leave_details';     }else{    $monthtit =  date('F Y',strtotime($_SESSION['frdt']));	//$frdt=date("Y-m-d", strtotime($_SESSION['frdt']));   // $todt=date("Y-m-d", strtotime($_SESSION['frdt']));    $tab_emp='leave_details'; }  //te = tab_emp, e = employee,mc = mast_company,t3= mast_client,mb = mast_bank  $desc = "SALARY FOR THE MONTH ".$monthtit; if($emp=='Parent')	{		 $setSql= "SELECT te.bankacno,te.bankacno as sender_bankacno,mb.ifsc_code,concat(".chr(34).chr(39).chr(34).",te.`bankacno`)as beneficiary_acno ,'10' as 'Account Type',concat(e.first_name,' ',e.middle_name,' ',e.last_name) AS 'Beneficiary Customer Name', e.pin_code as 'Beneficiary Customer Address',from_date,to_date FROM $tab_emp te inner join employee e on e.emp_id = te.emp_id inner join mast_client t3 on te.client_id= t3.mast_client_id inner JOIN mast_bank mb on te.bank_id = mb.mast_bank_id where t3.parentid='".$clintid."' and te.pay_mode = 'N' and te.from_date >= '$frdt' and te.to_date <='$to_date'";	}else{ 	 	  $setSql= "SELECT te.bankacno,te.bankacno as sender_bankacno,mb.ifsc_code,concat(".chr(34).chr(39).chr(34).",te.`bankacno`)as beneficiary_acno ,'10' as 'Account Type',concat(e.first_name,' ',e.middle_name,' ',e.last_name) AS 'Beneficiary Customer Name', e.pin_code as 'Beneficiary Customer Address',from_date,to_date FROM $tab_emp te inner join employee e on e.emp_id = te.emp_id inner join mast_client t3 on te.client_id= t3.mast_client_id inner JOIN mast_bank mb on te.bank_id = mb.mast_bank_id where te.client_id='".$clintid."' and te.pay_mode = 'N' and te.from_date >= '$frdt' and te.to_date <='$to_date'";	 	}      $setRec = mysql_query($setSql);$setCounter = mysql_num_fields($setRec);$setMainHeader="";$setData="";for ($i = 0; $i < $setCounter; $i++) {    $setMainHeader .= mysql_field_name($setRec, $i)."\t";}while($rec = mysql_fetch_row($setRec))  {    $rowLine = '';    foreach($rec as $value)       {        if(!isset($value) || $value == "")  {            $value = "\t";			        }   else  {//It escape all the special charactor, quotes from the data.     //       $value = strip_tags(str_replace('"', '""', $value));     //       $value = '"' . $value . '"' . "\t";	 	        $value = strip_tags(str_replace('"', '""', $value));            $value = '' . $value . '~' . "\t";	         }        $rowLine .= $value;    }    //$setData .= trim($rowLine)."\n";$setData .= trim($rowLine).chr(13).chr(10);}$setData = str_replace("\r", "", $setData);if ($setData == "") {    $setData = "\nno matching records found\n";}//This Header is used to make data download instead of display the dataheader("Content-type: application/octet-stream");header("Content-Disposition: attachment; filename=".$setExcelName.".xls");header("Pragma: no-cache");header("Expires: 0");//It will print all the Table row as Excel file row with selected column name as header.echo ucwords($setMainHeader)."\n".$setData."\n";?>