<?php //error_reporting(0);session_start();include("../lib/connection/db-config.php");include("../lib/class/user-class.php");$setCounter = 0;$userObj=new user();$setCounter = 0;$month=$_SESSION['month'];$comp_id=$_SESSION['comp_id'];$user_id=$_SESSION['log_id'];$setExcelName = "employee_detail";$emp=$_POST['emp'];//$res=$userObj->showEmployeereport($comp_id,$user_id);$clintid=$_SESSION['clintid'];$resclt=$userObj->displayClient($clintid);$cmonth=$resclt['current_month'];if($month=='current'){	$monthtit =  date('F Y',strtotime($cmonth));    $frdt=$cmonth;    $todt=$cmonth;    $tab_emp='tran_employee';    $tab_empded='tran_deduct';	$tab_empinc='tran_income';    $esifrdt=$cmonth;	$tab_days='tran_days';     }else{    $monthtit =  date('F Y',strtotime($_SESSION['frdt']));	$frdt=date("Y-m-d", strtotime($_SESSION['frdt']));    $todt=date("Y-m-d", strtotime($_SESSION['frdt']));    $tab_emp='hist_employee';    $tab_empded='hist_deduct';	$tab_empinc='hist_income';	$tab_days='hist_days'; }  //te = tab_emp, e = employee,mc = mast_company,t3= mast_client,mb = mast_bank  $desc = "SALARY FOR THE MONTH ".$monthtit; if($emp=='Parent')	{	 $setSql= "SELECT te.netsalary,mc.bankacno as sender_bankacno,mb.ifsc_code,concat(".chr(34).chr(39).chr(34).",te.`bankacno`)as beneficiary_acno ,'10' as 'Account Type',concat(e.first_name,' ',e.middle_name,' ',e.last_name) AS 'Beneficiary Customer Name', e.pin_code as 'Beneficiary Customer Address','".$desc ."' as 'Sender to Receiver Information', mc.comp_name as 'Originator Of Remittance' FROM $tab_emp te inner join employee e on e.emp_id = te.emp_id inner join mast_company mc on te.comp_id = mc.comp_id   	inner join mast_client t3 on te.client_id= t3.mast_client_id 	 inner JOIN mast_bank mb on te.bank_id = mb.mast_bank_id where t3.parentid='".$clintid."' and e.pay_mode = 'N'"; 			}else{ 	 	echo  $setSql= "SELECT te.netsalary,mc.bankacno as sender_bankacno,mb.ifsc_code,concat(".chr(34).chr(39).chr(34).",te.`bankacno`)as beneficiary_acno ,'10' as 'Account Type',concat(e.first_name,' ',e.middle_name,' ',e.last_name) AS 'Beneficiary Customer Name', e.pin_code as 'Beneficiary Customer Address','".$desc ."' as 'Sender to Receiver Information', mc.comp_name as 'Originator Of Remittance' FROM $tab_emp te inner join employee e on e.emp_id = te.emp_id inner join mast_company mc on te.comp_id = mc.comp_id  inner JOIN mast_bank mb on te.bank_id = mb.mast_bank_id where te.client_id='".$clintid."' and e.pay_mode = 'N'"; 	 	}      $setRec = mysql_query($setSql);$setCounter = mysql_num_fields($setRec);$setMainHeader="";$setData="";for ($i = 0; $i < $setCounter; $i++) {    $setMainHeader .= mysql_field_name($setRec, $i)."\t";}while($rec = mysql_fetch_row($setRec))  {    $rowLine = '';    foreach($rec as $value)       {        if(!isset($value) || $value == "")  {            $value = "\t";			        }   else  {//It escape all the special charactor, quotes from the data.     //       $value = strip_tags(str_replace('"', '""', $value));     //       $value = '"' . $value . '"' . "\t";	 	        $value = strip_tags(str_replace('"', '""', $value));            $value = '' . $value . '~' . "\t";	         }        $rowLine .= $value;    }    //$setData .= trim($rowLine)."\n";$setData .= trim($rowLine).chr(13).chr(10);}$setData = str_replace("\r", "", $setData);if ($setData == "") {    $setData = "\nno matching records found\n";}//This Header is used to make data download instead of display the dataheader("Content-type: application/octet-stream");header("Content-Disposition: attachment; filename=".$setExcelName.".xls");header("Pragma: no-cache");header("Expires: 0");//It will print all the Table row as Excel file row with selected column name as header.echo ucwords($setMainHeader)."\n".$setData."\n";?>